//SPDX-License-Identifier: MIT
pragma solidity 0.8.9;
import "@api3/airnode-protocol/contracts/rrp/requesters/RrpRequesterV0.sol";

contract CoinFlipBchain {
    // Mapping to store the balance of each user
    mapping (address => uint) public userBalances;

    // The total balance of the contract
    uint public contractBalance;

    // Deposit function
    function deposit(uint _amount) public {
        // Update the user's balance
        userBalances[msg.sender] += _amount;
        // Update the contract's total balance
        contractBalance += _amount;
    }

    // Withdraw function
    function withdraw(uint _amount) public {
        // Check if the user has sufficient balance to withdraw
        require(userBalances[msg.sender] >= _amount, "Insufficient balance");
        // Update the user's balance
        userBalances[msg.sender] -= _amount;
        // Update the contract's total balance
        contractBalance -= _amount;
    }

    // GetContractBalance function
    function getContractBalance() public view returns (uint) {
        return contractBalance;
    }

    // GetBalanceUser function
    function getBalanceUser(address _user) public view returns (uint) {
    return userBalances[_user];
    }



}

